import { createContext, useState } from 'react';

// actual value we want to access
export const UserContext = createContext({
  currentUser: null,
  setCurrentUser: () => null,
});

// This is an alias component that allows us use the UserContext.Provider and wrap the {children}.
// The .Provider is supplied to any created UserContext, and is a component
// that will wrap around any other comps that need access to the values inside.

export const UserProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  // instatiate this value as an object. 
  const value = { currentUser, setCurrentUser };

  return <UserContext.Provider value={value}>{children}</UserContext.Provider>;
};
