// we need useState to track the input from the input fields
import { useState } from 'react';

const defaultFormFields = {
  displayName: '',
  email: '',
  password: '',
  confirmPassword: ''
}

const SignUpForm = () => {
  // pass usestate the defaultFormFields value, which is an object with the above values
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { displayName, email, password, confirmPassword } = formFields; // init values, which will change.

  const handleChange = (event) => {
    // the name will come through on the event. 
    // So we can tell our setState which of these fields to update
    const { name } = event;
  };

  return (
    <div>
      <h1>
        Sign up with Email
      </h1>
      <form onSubmit={() => { }}>

        <label>Display Name</label>
        <input
          type='text'
          required
          onChange={handleChange}
          name='displayName'
          // the value is ultimately what gets displayed in the input field
          // we use the values we pulled off our formFields
          // the value from the state is the value that's shown on the input
          value={displayName} /> 

        <label>Email</label>
        <input
          type='email'
          required
          onChange={handleChange}
          name='email'
          value={email} /> 

        <label>Password</label>
        <input
          type='password'
          required
          onChange={handleChange}
          name='password'
          value={password} />

        <label>Confirm Password</label>
        <input
          type='confirm-password'
          required
          onChange={handleChange}
          name='confirmPassword'
          value={confirmPassword} />

        <button type='submit'>Sign Up</button>

      </form>
    </div>
  );
}

export default SignUpForm;
